pragma solidity ^0.8.0;

import "./UsingOracle.sol";

contract DragonBlockOracle {

    struct OracleRequest {
        bool done;
        uint256 yesVote;
        uint256 noVote;
        address userToVerify;
        address callerContract;
    }

    mapping(address => bool) private verifiedUsers;

    mapping(uint256 => mapping (address => bool)) voters;

    OracleRequest[] public requests;

    event UserVerification(address, uint256);

    address public minter;

    constructor() {
        minter = msg.sender;
        verifiedUsers[msg.sender] = true;
    }

    function verifyUser(address _userAddress, address _callerContract) public {

        requests.push(OracleRequest({
            done: false,
            yesVote: 0,
            noVote: 0,
            userToVerify: _userAddress,
            callerContract: _callerContract
